import React, { Component } from 'react';

class Board extends Component {
    render() {
        return <div className="game-board">
            <div className="row">
                <div className="square" data-square={0} onClick={this.handlePick} />
                <div className="square" data-square={1} onClick={this.handlePick} />
                <div className="square" data-square={2} onClick={this.handlePick} />
            </div>
            <div className="row">
                <div className="square" data-square={3} onClick={this.handlePick} />
                <div className="square" data-square={4} onClick={this.handlePick} />
                <div className="square" data-square={5} onClick={this.handlePick} />
            </div>
            <div className="row">
                <div className="square" data-square={6} onClick={this.handlePick} />
                <div className="square" data-square={7} onClick={this.handlePick} />
                <div className="square" data-square={8} onClick={this.handlePick} />
            </div>
        </div>;
    }
    handlePick = (click) => {
        const arrayPosition = click.target.dataset.square;
        this.props.getPosition(arrayPosition)
    }
}

export default Board;
