import React, { Component } from 'react';
import './App.css';
import Board from './Components/Board';


class App extends Component {
  state = {
    turn: 'x',
    board: ['', '', '', '', '', '', '', '', ''],
    gameOver: false
  }
  render() {
    return (
      <div className="App">
        <h1>Noughts & Crosses</h1>
        <Board getPosition={this.getPosition}/>
      </div>
    );
  }
  getPosition = (position) => {
    this.updateBoardState(position);
    // console.log(this.state, 'inside getPosition');
  }
  updateBoardState = (position) => {
    // console.log(position)
    // console.log(this.state, 'state inside updateBoard')
    this.setState(({board, turn}) => {
      console.log(board, turn, position)
      const updatedBoard = board.map((item, index) => {
        console.log(index)
        if (index.toString() !== position) {
          console.log('here')
        }  
        return item;
      })
      console.log(updatedBoard)
      return {
        board: updatedBoard
      }
    })
  }
}

export default App;
